mapper.select:
SELECT T.ID,T.SID,T.SNAME,T.SCLASS,T.SSEX,T.SPHONE,T.CID,T.TID, T.SCORE1,T.SCORE2,T.SCORE3,T.SCORE4 FROM GP_STUDENTS T
(T.SID = #{text} OR T.SNAME = #{text})
AND T.TID = #{tid}
ORDER BY ${sortname} ${sortvalue}

SELECT 
COUNT(1) 
FROM GP_STUDENTS T
WHERE T.SID = #{sid}
AND T.TID = #{tid}

mapper.update:
UPDATE GP_STUDENTS

	SCORE1 = #{score1},
	SCORE2 = #{score2},
	SCORE3 = #{score3},
	SCORE4 = #{score4}

WHERE SID = #{sid}
